class VDOM {
	constructor(tag, attrs, children) {
  	this.tag = tag;
    this.attrs = attrs;
    this.children = children;
    this.dom = null;
  }
}

const d = (tag, attrs, children) => new VDOM(tag, attrs, children);
const t = (text) => new VDOM("text", {text}, [])

const dom = d("span", {}, [t("Hello! ")]);

