  render(parent) {
    // this.domã‚’ä½œã‚‹
    this.initElement();
    this.updateAttrs();
    // è¦ªè¦ç´ ã«domã‚’æŒ¿å…¥ã™ã‚‹
    parent.appendChild(this.dom);
    // å­è¦ç´ ã‚’åæ˜ ã™ã‚‹
    for (let child of this.children) {
			child.render(this.dom);
